od=\'POST\';document.frm.submit();return false;}}\nelse return true\n}</SCRIPT>\n<INPUT TYPE=\"button\" NAME=\"EMAIL\" VALUE=\"Press to SEND EMAIL\" onClick=checkValues()>\n<INPUT TYPE=\"HIDDEN\" NAME=\"EMAILADDRESS\" VALUE=\"None\">\n  </TR>\n</TABLE></CENTER>\n<SCRIPT  LANGUAGE=\"Javascript\">\nfunction checkValues(){var v1 = document.frm.MAILADDY.value;errorOccured = false;var email = /(\\w+)@(\\w+)/;\nif(v1.length == 0){errorOccured = true; alert(\"No email address has been entered. Please enter an email address\")\ndocument.frm.MAILADDY.focus();}\nelse if(email.test(document.frm.MAILADDY.value)== false){\nerrorOccured = true; alert(\"Please enter a valid email\")\ndocument.frm.MAILADDY.focus();}\nif(!errorOccured){document.frm.EMAILADDRESS.value = \"EMAIL\"\ndocument.frm.method=\'POST\';document.frm.submit()}}</SCRIPT>\n</FORM><BR><FORM NAME=\"FAKEFORM\">\n<INPUT TYPE=\"HIDDEN\" NAME=\"PS\" VALUE=\'0\'>\n</FORM>\n<CENTER>\n<FONT face=Default size=3><A HREF=\"/help/disphlp3.htm\"><font size=\"-1\" face=\"Arial, Helvetica\">Help</font></A>&nbsp;-&nbsp;<A HREF=\"/cgi-bin/Pwebrecon.cgi?PAGE=sbSearch&SEQ=20120413205558&PID=cLxm22JiZh7PFIt2BmPBMWAxdB5\"><font size=\"-1\" face=\"Arial, Helvetica\">Search</font></A>&nbsp;-&nbsp;<A HREF=\"/cgi-bin/Pwebrecon.cgi?PAGE=HISTORY&SEQ=20120413205558&PID=cLxm22JiZh7PFIt2BmPBMWAxdB5\"\n><font size=\"-1\" face=\"Arial, Helvetica\">History</font></A>&nbsp;-&nbsp;<A><I><font size=\"-1\" face=\"Arial, Helvetica\">Headings</font></I></A>&nbsp;-&nbsp;<A HREF=\"/cgi-bin/Pwebrecon.cgi?ti=1,0&Search%5FArg=9781590596715&Search%5FCode=STNO&CNT=100&PID=cLxm22JiZh7PFIt2BmPBMWAxdB5&SEQ=20120413205558&SID=1\"\nA><font size=\"-1\" face=\"Arial, Helvetica\">Titles</font></A>&nbsp;-&nbsp;<A HREF=\"/cgi-bin/Pwebrecon.cgi?PAGE=REQUESTBIB&SEQ=20120413205558&PID=cLxm22JiZh7PFIt2BmPBMWAxdB5&bibId=14728289&search=true&v1=1&ti=1,1&SEQ=20120413205558&Search%5FArg=9781590596715&Search%5FCode=STNO&CNT=100&PID=cLxm22JiZh7PFIt2BmPBMWAxdB5&SID=1\"\n><font siz